import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import PlantMenu from '../components/plant_menu';
import '../App.css'

function Search({navigation}) {
  const [name, setName] = useState("");
  const [plants, setPlant] = useState([]);

  const loadPlant = async () => {
      const response = await fetch(`/plants`);
      const data = await response.json();
      setPlant(data);
  };

  useEffect(() => {
      loadPlant();
  }, []);


  ///const filtered = plants.filter(plant => plant.Name = `${name}`);

  let names = plants.map(plant => plant.Name);

  const [foundName, setFoundName] = useState(names);

  const listPlants = names.map((plantName) =>
    <li>{plantName}</li>

  );
  const filter = (e) => {
    const keyword = e.target.value;

    if (keyword !== '') {
      const results = names.filter((name) => {
        return name.name.toLowerCase().startsWith(keyword.toLowerCase());
        // Use the toLowerCase() method to make it case-insensitive
      });
      setFoundUsers(results);
    } else {
      setFoundUsers(names);
      // If the text field is empty, show all users
    }

    setName(keyword);
  };

  return (
    <body>
      <h1>Plant Search</h1>
      <input type="text"
      placeholder='Type plant name...'
      onChangeText={(val) => setName(val)}/>
      <button onClick={(name) => loadPlant(name)}>Search</button>
      <ul>{listPlants}</ul>

    </body>
  );
}

export default Search;
