import React, { useState, useEffect } from 'react';
import '../App.css'
import { Link } from 'react-router-dom';
import PlantList from '../components/plant_list';
import { useHistory } from 'react-router-dom';
import axios from 'axios';

let ip;

function Home({setPlantToEdit}) {
      const [plants, setPlant] = useState([]);
      const history = useHistory();
      const [location, setLocation] = useState([]);
      const [temp, setTemp] = useState([]);


      const onDelete = async _id => {
        const response = await fetch(`/plants/${_id}`, { method: 'DELETE' });
        if (response.status === 204) {
            setPlant(plants.filter(m => m._id !== _id));
        } else {
            console.error(`Failed to delete plant with _id = ${_id}, status code = ${response.status}`);
        }
    };


      const onEdit = plant => {
          setPlantToEdit(plant);
          history.push("/edit-plant");
      }

      //creating IP state
      const [ip, setIP] = useState('');

      //creating function to load ip address from the API
      const getData = async () => {
        const res = await axios.get('https://geolocation-db.com/json/')
        console.log(res.data);
        setIP(res.data.IPv4)

      }

      useEffect( () => {
        //passing getData method to the lifecycle method
        getData()

      }, []);

      let locationURL = "http://localhost:5002/location_service/?ip_address=" + {ip}
      //creating function to load location from the API
      const getLocal = async () => {
        const res = await axios.get(locationURL)
        console.log(res.data.city);
        setLocation(res.data.city)

      }

      useEffect( () => {
        //passing getData method to the lifecycle method
        getLocal()

      }, []);

      const loadPlant = async () => {
          const response = await fetch('/plants');
          const data = await response.json();
          setPlant(data);
      };

      useEffect(() => {
          loadPlant();
      }, []);

      return (
          <>
              <h2>List of Plants</h2>
              <h3>My Location: {location}</h3>
               <button onClick={e => setLocation(location)}>Update Location</button>
              <PlantList plants={plants}onDelete={onDelete}onEdit={onEdit}></PlantList>

          </>
      );
  }

export default Home;
