import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import PlantMenu from '../components/plant_menu';
import '../App.css'

function Search({navigation}) {
  const [name, setName] = useState("");
  const [plants, setPlant] = useState([]);

  const loadPlant = async () => {
      const response = await fetch(`/plants`);
      const data = await response.json();
      setPlant(data);
  };

  useEffect(() => {
      loadPlant();
  }, []);


  const filtered = plants.filter(plant => plant.Name = `${name}`);

  let names = plants.map(plant => plant.Name);

  const listPlants = names.map((plantName) =>
    <li>{plantName}</li>
  );


  return (
    <>
      <h1>Plant Search</h1>
      <input type="text"
      placeholder='Type plant name...'
      onChangeText={(val) => setName(val)}/>
      <button onClick={(name) => loadPlant(name)}>Search</button>
      <ul>{listPlants}</ul>
    </>
  );
}

export default Search;
